    }
    
    func testSearchResultsValidInvalidJSON() {
        let json:[String: Any] = [ "" : "" ]
        XCTAssert(SearchResults(json: json) == nil)
    }
    
    func testSearchResultsValidJSON() {
        guard let data = jsonString.data(using:.utf8) else {
            XCTAssert(false);
            return;
        }
        guard let json = try? JSONSerialization.jsonObject(with: data, options: []) as? [String: Any] else {
            XCTAssert(false);
            return;
        }
        guard let items = json!["items"] as? [[String: Any]] else {
            XCTAssert(false);
            return;
        }
        XCTAssert(items.count > 0);
        for item in items {
            XCTAssert(SearchResults(json: item) != nil)
        }
    }
